// NameSearchForm.js
import React, { useState } from 'react';
import { collection, getDocs, addDoc, query, where} from "firebase/firestore";
import { db } from './firebase';

const Test = () => {
  const [searchValue, setSearchValue] = useState('');
  const [searchResult, setSearchResult] = useState([]);
  
  

  const handleSearch = async (e) => {
    e.preventDefault();
    try{
    const data = await getDocs(collection(db, 'compColl'));
    const q = query(collection(db, 'compColl'), where('nameC', '==', 'KF'));
    const search2 = await getDocs(q)
    const search = data.docs.map(doc => ([{
      ...doc.data()}].filter((object) => object.nameC === "KF") 
    ))
    
    const data2 = search2.docs.map(doc => ({
      id: doc.id,
      ...doc.data()
    }));
    console.log(search)
    console.log(data2)
  }
    catch(error){
    console.error('Error fetching data:', error);
    return [];
  }

    /*const compCollRef = addDoc(collection(db, 'compColl'), {
        name:"hi",
        email:"hi",
        message:"hi",
      }).then(() => {
        alert("success")
      }).catch((error) => alert(error.message));*/
      
    

    /*try {
      // Search for documents where nameAR matches the search value
      const snapshotAR = await compCollRef
        .where('nameAR', '==', searchValue)
        .get();
      const documentsAR = snapshotAR.docs.map(doc => doc.data());

      // Search for documents where nameEN matches the search value
      const snapshotEN = await compCollRef
        .where('nameEN', '==', searchValue)
        .get();
      const documentsEN = snapshotEN.docs.map(doc => doc.data());

      // Search for documents where nameC matches the search value
      const snapshotC = await compCollRef
        .where('nameC', '==', searchValue)
        .get();
      const documentsC = snapshotC.docs.map(doc => doc.data());

      // Combine results from all queries
      const searchResults = [...documentsAR, ...documentsEN, ...documentsC];

      // Update state with search results
      setSearchResult(searchResults);
    } catch (error) {
      console.error('Error searching for documents:', error);
    }*/
  };

  

  return (
    <div>
      <form onSubmit={handleSearch}>
        <input
          type="text"
          value={searchValue}
          onChange={(e) => setSearchValue(e.target.value)}
          placeholder="Search by name"
        />
        <button type="submit">Search</button>
      </form>
      {searchResult.length > 0 && (
        <div>
          <h2>Search Results:</h2>
          <ul>
            {searchResult.map((result, index) => (
              <li key={index}>{result.nameAR}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default Test;
