import React, { useState } from 'react';
import sqlite3 from 'sqlite3';

const SQLiteDataTable = () => {
  const [data, setData] = useState([]);
  const [searchValue, setSearchValue] = useState('');
  const [selectedCategory, setSelectedCategory] = useState(null);

  

  const handleRadioChange = (category) => {
    setSelectedCategory(category);
  };

  const handleSearchInputChange = (e) => {
    setSearchValue(e.target.value);
  };

  const handleSearch = () => {
    if (!selectedCategory) {
      alert('You have to pick the category of the search');
      return;
    }

    const query = `
      SELECT *
      FROM your_table
      WHERE ${selectedCategory} = ?
    `;

    db.all(query, [searchValue], (err, rows) => {
      if (err) {
        console.error('Error fetching data:', err);
        return;
      }

      setData(rows);
    });
  };

  return (
    <div>
      <h2>Data from SQLite Database:</h2>
      <div>
        <input type="text" value={searchValue} onChange={handleSearchInputChange} />
        <button onClick={handleSearch}>Search</button>
      </div>
      <div>
        <label>
          <input type="radio" name="category" value="nameEN" checked={selectedCategory === 'nameEN'} onChange={() => handleRadioChange('nameEN')} />
          English
        </label>
        <label>
          <input type="radio" name="category" value="nameAR" checked={selectedCategory === 'nameAR'} onChange={() => handleRadioChange('nameAR')} />
          Arabic
        </label>
        <label>
          <input type="radio" name="category" value="nameC" checked={selectedCategory === 'nameC'} onChange={() => handleRadioChange('nameC')} />
          Code
        </label>
      </div>
      {data.map((row, index) => (
        <div key={index} style={{ marginBottom: '20px' }}>
          <h3>{`Row ${index + 1}`}</h3>
          <table>
            <tbody>
              {Object.entries(row).map(([key, value]) => (
                <tr key={key}>
                  <th>{key}</th>
                  <td>{value}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ))}
    </div>
  );
};

export default SQLiteDataTable;
