// NameSearchForm.js
import React, { useState } from 'react';
import { collection, getDocs, addDoc, query, where} from "firebase/firestore";
import { db } from './firebase';

const Test = () => {
  const [searchValue, setSearchValue] = useState('');
  const [data, setData] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);

  const handleRadioChange = (category) => {
    setSelectedCategory(category);
  };



  const handleSearch = async (e) => {
    e.preventDefault();

    let searchField = null;

    // Determine which field to search based on selected category
    if (selectedCategory === 'english') searchField = 'nameEN';
    if (selectedCategory === 'arabic') searchField = 'nameAR';
    if (selectedCategory === 'code') searchField = 'nameC';

    
    
    try{
    const q = query(collection(db, 'compColl'), where('nameC', '==', searchValue));
    const docs = await getDocs(q)
    
    const searchR = docs.docs.map(doc => ({
      ...doc.data()
    })); 
    setData(searchR)
  }
    catch(error){
    console.error('Error fetching data:', error);
    return [];
  }



    /*const compCollRef = addDoc(collection(db, 'compColl'), {
        name:"hi",
        email:"hi",
        message:"hi",
      }).then(() => {
        alert("success")
      }).catch((error) => alert(error.message));*/
      
    

    /*try {
      // Search for documents where nameAR matches the search value
      const snapshotAR = await compCollRef
        .where('nameAR', '==', searchValue)
        .get();
      const documentsAR = snapshotAR.docs.map(doc => doc.data());

      // Search for documents where nameEN matches the search value
      const snapshotEN = await compCollRef
        .where('nameEN', '==', searchValue)
        .get();
      const documentsEN = snapshotEN.docs.map(doc => doc.data());

      // Search for documents where nameC matches the search value
      const snapshotC = await compCollRef
        .where('nameC', '==', searchValue)
        .get();
      const documentsC = snapshotC.docs.map(doc => doc.data());

      // Combine results from all queries
      const searchResults = [...documentsAR, ...documentsEN, ...documentsC];

      // Update state with search results
      setSearchResult(searchResults);
    } catch (error) {
      console.error('Error searching for documents:', error);
    }*/
  };


  return (
    <div>
      <form onSubmit={handleSearch}>
        <input
          type="text"
          value={searchValue}
          onChange={(e) => setSearchValue(e.target.value)}
          placeholder="Search by name"
        />
        <button type="submit">Search</button>
      </form>
      <div>
        <label>
          <input type="radio" name="category" value="english" checked={selectedCategory === 'english'} onChange={() => handleRadioChange('english')} />
          English
        </label>
        <label>
          <input type="radio" name="category" value="arabic" checked={selectedCategory === 'arabic'} onChange={() => handleRadioChange('arabic')} />
          Arabic
        </label>
        <label>
          <input type="radio" name="category" value="code" checked={selectedCategory === 'code'} onChange={() => handleRadioChange('code')} />
          Code
        </label>
      </div>
      <div>
      {data.map(item => (
        <div key={item.id} style={{ marginBottom: '20px' }}>
          <h3>{`Document ID: ${item.nameEN}`}</h3>
          <table>
            <tbody>
              {Object.entries(item).map(([key, value]) => {
                return (
                  <tr key={key}>
                    <th>{key}</th>
                    <td>{value}</td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      ))}
    </div>
    </div>
  );
};

export default Test;
